import React from 'react'
import { MARCAS } from '../constans/index'

const Formulario = () => {
  return (
    <>
        <form>
            <div>
                <label className='label-marca'>Marca</label>

                <select
                 name="marca"
                 className='select-marca'
                 >
                <option value=""> --Selecciona Marca--</option>

                 { MARCAS.map( marca => (
                      <option
                      key={marca.id}
                      value={marca.id}
                      >
                          {marca.nombre}
                      </option>
                 ))}
                 </select>
            </div>
        </form>
    </>
  )
}

export default Formulario
