import {Fragment} from 'react'
import { MARCAS, YEARS, PLANES } from '../constans/index'
import { CotizadorProvider } from '../context/CotizadorProvider';
import useCotizador from '../hooks/useCotizador';



const Formulario = () => {

 
  const { handleChangeDatos } = useCotizador()

  return (
    <>
        <form>
            {/* LABEL 1 */}
            <div className='contenedor-inputs'>
                <label className='label-marca'>Marca</label>

                <select
                 name="marca"
                 className='form-select'
                 onChange={ e => (handleChangeDatos(e))}
                 >
                <option value=""> --Selecciona Marca--</option>

                 { MARCAS.map( marca => (
                      <option
                      key={marca.id}
                      value={marca.id}
                      >
                          {marca.nombre}
                      </option>
                 ))}
                 </select>
            </div>

            {/* LABEL 2 */}      
            <div className='contenedor-inputs'>
                <label className='label-marca'>Año</label>

                <select
                 name="year"
                 className='form-select'
                 onChange={ e => (handleChangeDatos(e))}
                 >
                <option value=""> --Selecciona año--</option>

                 { YEARS.map( year=> (
                      <option
                      key={year}
                      value={year}
                      >
                          {year}
                      </option>
                 ))}
                 </select>
            </div>

             {/* LABEL 3 */}           
            <div className='contenedor-inputs'>
                <label className='label-marca'>Elije un Plan</label>
                 { PLANES.map( plan=> (
                    <Fragment key={plan.id}>
                        <label
                        className='form-check-label'
                        >
                         {plan.nombre}
                         </label>

                        <input
                        className='form-check-input'
                         type="radio"
                         name='plan'
                         value={plan.id}
                         onChange={ e => (handleChangeDatos(e))}
                        
                        />
                    </Fragment>
                 ))}
                
            </div>

            <input
             type="submit"
             className='btn btn-primary boton-submit w-100'
             
            
            />
        </form>
    </>
  )
}

export default Formulario
